import { Component } from '@angular/core';
import { ProductService } from 'src/app/services/product.service';

@Component({
  selector: 'app-shop',
  templateUrl: './shop.component.html',
  styleUrls: ['./shop.component.scss'],
})
export class ShopComponent {

  Dropdown:any[] = [
    {
      title:'Sorting',
      category:['Latest', 'Popularity', 'Best Rating']
    },
    {
      title:'Showing',
      category:['10', '20', '30']
    },
  ];
  constructor(private service:ProductService){
    service.Breadcrumb.next([
      {
        pageTitle: 'Home',
        url: '',
      },
      {
        pageTitle: 'Shop',
        url: 'Shop/shop',
      },
      {
        pageTitle: 'Shop List',
        url: 'Shop/shop',
      }
    ]);
  }

  productInDesc(){
    this.service.getAllProductInDesc()
  }
}
<!-- Shop Start -->
<div class="container-fluid">
  <div class="row px-xl-5">
    <!-- Shop Sidebar Start -->
    <div class="col-lg-3 col-md-4">
      <!-- Price Start -->
      <app-filter-by-price></app-filter-by-price>
      <!-- Price End -->

      <!-- Color Start -->
      <app-filter-by-color></app-filter-by-color>
      <!-- Color End -->

      <!-- Size Start -->
      <app-filter-by-size></app-filter-by-size>
      <!-- Size End -->
    </div>
    <!-- Shop Sidebar End -->

    <!-- Shop Product Start -->
    <div class="col-lg-9 col-md-8">
      <div class="row pb-3">
        <app-product-list class="row"></app-product-list>
      </div>
    </div>
    <!-- Shop Product End -->
  </div>
</div>
<!-- Shop End -->
