import { Component } from '@angular/core';
import { ProductService } from '../services/product.service';

@Component({
  selector: 'app-page-not-found',
  templateUrl: './page-not-found.component.html',
  styleUrls: ['./page-not-found.component.scss']
})
export class PageNotFoundComponent {
  constructor(private service:ProductService) {
    service.Breadcrumb.next([])
  }
}
<p class="not-found mt-5">This Page is not Found!</p>
<p class="page-not-found">
  This Page is Currently not Working Right Now! <br />
  Please Redirect to Home Page....
</p>
<div class="text-center">
  <button class="btn btn-success font-weight-bold py-2" [routerLink]="['/']">
    Go To Home Page
  </button>
</div>
